import React from 'react';
import axios from 'axios';
import history from '../../History/history'
import './register.css'
import {Link} from 'react-router-dom'

function Register(){
      // username:'',
      // password:'',
      // email:'',
      // fname:'',
      // lname:''
      // signupCheck: "",
      // checkValue: false
  const [username,setUsername] = React.useState('');
  const [password,setPassword] = React.useState('');
  const [email,setEmail] = React.useState('');
  const [fname,setFname] = React.useState('');
  const [lname,setLname] = React.useState('');
  const [signupCheck,setSignupCheck] = React.useState('');
  const [checkValue,setCheckValue] = React.useState('');

  funccheckStatus = event => {
    setCheckValue(event.target.checked)
  }

  handleSubmit = event =>{
    event.preventDefault();
    const user = {
      username:this.state.username,
      password:this.state.password,
      email:this.state.email,
      fname:this.state.fname,
      lname:this.state.lname
    }
    if(this.state.checkValue){
      axios
        .post("http://localhost:8887/home/receivedata",user)
        .then(res=>{
          this.setState({signupCheck:res.data});
          console.log(this.state.signupCheck)
      })
      .then(()=>{(this.state.signupCheck === "Details entered")?history.push('/profile'):history.push('/')});
    } else {
      this.setState({signupCheck:"Please accept the Terms and Conditions"});
    }
  }
  handleChange = event => {
    this.setState({[event.target.name]:event.target.value});
  }
    return (
      <div class="main">
        <div className="content_rgt">
          <div className="register_sec">
            <h1>Create An Account</h1>
            <form onSubmit={this.handleSubmit}>
              <li><span>Username</span><input type="text" name="username" placeholder="Enter your username" onChange={this.handleChange} required/></li>
              <li><span>Password</span><input type="password" name="password" placeholder="Enter your password" onChange={this.handleChange} required/></li>
              <li><span>Email</span><input type="email" name="email" placeholder="Enter your email" onChange={this.handleChange} required/></li>
              <li><span>First Name</span><input type="text" name="fname" placeholder="Enter your first name" onChange={this.handleChange} required/></li>
              <li><span>Last Name</span><input type="text" name="lname" placeholder="Enter your last name" onChange={this.handleChange} required/></li>
              <li><input type="checkbox" onChange={this.checkStatus}/>I agree to Term &amp; Conditions</li>
              {this.state.signupCheck==="Details entered"?(
                <h4 className="green">{this.state.signupCheck}</h4>
                ):(
                <h4 className="red">{this.state.signupCheck}</h4>
              )}
              <li><input type="submit" defaultValue="Register" /></li>
            </form>
            <div className="addtnal_acnt">I already have an account.<Link to="/login">Login My Account !</Link></div>
          </div>
        </div>
      </div>
    );
}

export default Register;
