    super(props);
    this.state = {
      imageName:'',
      description:'',
      filename:'',
      date: new Date(),
      uploadedFile:{}
    }
    this.state = {
      imageStatus: ''
    }
  }

  handleSubmit = event =>{
    event.preventDefault();
    //console.log(event.target[2].files[0].name);
    const user = {
      imageName:this.state.imageName,
      description:this.state.description,
      filename:event.target[2].files[0].name,
      date: new Date(),
    }
    axios
      .post("http://localhost:8887/home/uploadimage",user)
