import React from 'react'
import axios from 'axios'
import './upload.css'

export default class UploadImage extends React.Component{

  constructor(props){
    super(props);
    this.state = {
      email:'',
      imageName:'',
      description:'',
      filename:'',
      date: new Date(),
      uploadedFile:''
    }
    this.state = {
      imageStatus: ''
    }
  }

  handleSubmit = event =>{
    event.preventDefault();
    const formData = new FormData();
    formData.append('email',localStorage.getItem('email'));
    formData.append('imageName',this.state.imageName);
    formData.append('description',this.state.description);
    formData.append('filename',event.target[2].files[0].name);
    formData.append('date',new Date());
    formData.append('uploadedFile',this.state.uploadedFile);
    console.log(event.target[2].files[0]);
    console.log(formData);

    axios
      .post("http://localhost:8887/home/uploadimage",formData,{
        headers:{
          "Content-Type":"multipart/form-data"
        }
      })
      .then(res=>{
        //console.log(res);
        this.setState({imageStatus:res.data});
      })

      var form = document.getElementById('uploadImage');
      form.reset();
  }

  handleChange = event => {
    this.setState({[event.target.name]:event.target.value});
  }

  render(){
    return(
      <div class="upload-container">
        <form onSubmit={this.handleSubmit} id="uploadImage" enctype="multipart/form-data">
          <label className="label1">Name:</label>
          <input type='text' name="imageName" onChange={this.handleChange} required />
          <label className="label1">Description:</label>
          <textarea name="description" onChange={this.handleChange} /><br/>
          <div className="category">
            <input type="radio" id="dog" name="categories" value="dog"/>
            <label for="dog">Dogs</label>
            <input type="radio" id="cat" name="categories" value="cat"/>
            <label for="cat">Cats</label>
            <input type="radio" name="categories" value="bird"/>
            <label for="bird">Birds</label>
            <input type="radio" name="categories" value="rabbit"/>
            <label for="rabbit">Rabbit</label>
            <input type="radio" id="other" name="categories" value="other"/>
            <label for="other">Other</label>
          </div>
          <input type='file' name='uploadedFile' onChange={event=>{
            this.setState({uploadedFile:event.target.files[0]});
          }}  required />
          {this.state.imageStatus==="Image Uploaded"?(
            <h4 className="green">{this.state.imageStatus}</h4>
            ):(
            <h4 className="red">{this.state.imageStatus}</h4>
          )}
          <input id="submit-image" type='submit' value='submit'/>
        </form>
      </div>
    );
  }
}
