import React from 'react'
import axios from 'axios'
import './upload.css'
import ImageHolder from '../Timeline/imageholder'
import ReactDOM from 'react-dom'

export default function UploadImage(){

  const [addImage,setAddImage] = React.useState([])

  function imageHolder(){
    return(
      <div>
        <div className="contnt_2">
          <div className="div_a">
            <div className="div_title">User Interface PSD Source files Web Designing for web</div>
            <div className="btm_rgt">
              <div className="btm_arc">Cats</div>
            </div>
            <div className="div_top">
              <div className="div_top_lft"><img src="images/img_6.png" />Steave Waugh</div>
              <div className="div_top_rgt"><span className="span_date">02 Jan 2014</span><span className="span_time">11:15am</span></div>
            </div>
            <div className="div_image"><img src="images/lft_img.png" alt="pet" /></div>
            <div className="div_btm">
              <div className="btm_list">
                <ul>
                  <li><a href="#"><span className="btn_icon"><img src="images/icon_001.png" alt="share" /></span>Share</a></li>
                  <li><a href="#"><span className="btn_icon"><img src="images/icon_002.png" alt="share" /></span>Flag</a></li>
                  <li><a href="#"><span className="btn_icon"><img src="images/icon_003.png" alt="share" /></span>0 Likes</a></li>
                  <li><a href="#"><span className="btn_icon"><img src="images/icon_004.png" alt="share" /></span>4 Comments</a></li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  const [imageName,setImageName] = React.useState('');
  const [description,setDescription] = React.useState('');
  const [category,setCategory] = React.useState('');
  const [uploadedFile,setUploadedFile] = React.useState('');
  const [imageStatus,setImageStatus] = React.useState('');

  function handleSubmit(event){
    event.preventDefault();
    const formData = new FormData();
    formData.append('email',localStorage.getItem('email'));
    formData.append('imageName',imageName);
    formData.append('description',description);
    formData.append('filename',event.target[7].files[0].name);
    formData.append('category',category);
    formData.append('date',new Date());
    formData.append('uploadedFile',uploadedFile);
    console.log(event.target[7].files[0]);
    console.log(formData);

    axios
      .post("http://localhost:8887/home/uploadimage",formData)
      .then(res=>{
        //console.log(res);
        setImageStatus(res.data);
      })

      var form = document.getElementById('uploadImage');
      form.reset();

      const picture = React.createElement(imageHolder,{});
      setAddImage.map((image,index)=>{
        return (
          <ImageHolder key={index} />
        )
      })

      function addingImage(){
        setAddImage([...addImage],[])
      }
      addingImage();

  }
    return(
      <div class="upload-container">
        <form onSubmit={handleSubmit} id="uploadImage" enctype="multipart/form-data">
          <label className="label1">Name:</label>
          <input type='text' name="imageName" onChange={event=>setImageName(event.target.value)} required />
          <label className="label1">Description:</label>
          <textarea name="description" onChange={event=>setDescription(event.target.value)} />
          <label className="label1">Categories:</label>
          <div className="category">
            <input type="radio" id="dog" name="categories" value="dog" onChange={event=>setCategory(event.target.value)} />
            <label for="dog">Dogs</label>
            <input type="radio" id="cat" name="categories" value="cat" onChange={event=>setCategory(event.target.value)} />
            <label for="cat">Cats</label>
            <input type="radio" name="categories" value="bird" onChange={event=>setCategory(event.target.value)} />
            <label for="bird">Birds</label>
            <input type="radio" name="categories" value="rabbit" onChange={event=>setCategory(event.target.value)} />
            <label for="rabbit">Rabbit</label>
            <input type="radio" id="other" name="categories" value="other" onChange={event=>setCategory(event.target.value)} />
            <label for="other">Other</label>
          </div>
          <input type='file' name='uploadedFile' onChange={event=>{
            setUploadedFile(event.target.files[0]);
          }}  required />
          {imageStatus==="Image Uploaded"?(
            <h4 className="green">{imageStatus}</h4>
            ):(
            <h4 className="red">{imageStatus}</h4>
          )}
          <input id="submit-image" type='submit' value='submit'/>
        </form>
      </div>
    );
}
