import React, { useState, useEffect } from "react";
import { Link, Switch, Route, Router } from "react-router-dom";
import UploadImage from "../ImageUploadForm";
import axios from "axios";
import time from "../../TimeFunctions";
import config from "../../Config/config";
import action from "../../Action/action";
import TimelineLeft from "../TimelineLeft/TimelineLeft";
import TimelineRoutes from "../../Routes/TimelineRoutes";

function Timeline(props) {
  const [uploadImage, setUploadImage] = useState(false);
  const [dataOnUser, setDataOnUser] = useState([]);
  const [userName, setUserName] = useState("");
  const [value, setValue] = useState(false);

  useEffect(() => {
    // const id = localStorage.getItem("email");
    axios.post(`${config.backendUrl}getpost`).then((res) => {
      setDataOnUser(res.data);
      console.log("pop");
      console.log(res.data);
    });
  }, [value, props.uploadImage]);

  // useEffect(() => {
  //   const id = localStorage.getItem("email");
  //   axios.post(`${config.backendUrl}userdata`, { email: id }).then((res) => {
  //     setUserName(res.data.fname + " " + res.data.lname);
  //   });
  // }, []);

  function toggleValue() {
    if (value === false) {
      setValue(true);
    } else {
      setValue(false);
    }
  }
  return (
    <div>
      <div>
        <div className="container">
          <div className="content">
            <div className="content_rgt">
              <div className="rght_btn">
                {" "}
                <span className="rght_btn_icon">
                  <img src="images/btn_iconb.png" alt="up" />
                </span>{" "}
                <span className="btn_sep">
                  <img src="images/btn_sep.png" alt="sep" />
                </span>{" "}
                <Link
                  onClick={() => {
                    setUploadImage(!uploadImage);
                    props.dispatch(action.uploadImage(uploadImage));
                  }}
                >
                  Upload Post
                </Link>{" "}
              </div>
              {uploadImage === true ? (
                <UploadImage toggleValue={toggleValue} />
              ) : null}
              <div className="rght_btn">
                {" "}
                <span className="rght_btn_icon">
                  <img src="images/btn_icona.png" alt="up" />
                </span>{" "}
                <span className="btn_sep">
                  <img src="images/btn_sep.png" alt="sep" />
                </span>{" "}
                <Link to="#">Invite Friends</Link>{" "}
              </div>
              <div className="rght_cate">
                <div className="rght_cate_hd" id="rght_cat_bg">
                  Categories
                </div>
                <div className="rght_list">
                  <ul>
                    <li>
                      <a href="#">
                        <span className="list_icon">
                          <img src="images/icon_01.png" alt="up" />
                        </span>{" "}
                        CATS
                      </a>
                    </li>
                    <li>
                      <a href="#">
                        <span className="list_icon">
                          <img src="images/icon_02.png" alt="up" />
                        </span>{" "}
                        Dogs
                      </a>
                    </li>
                    <li>
                      <a href="#">
                        <span className="list_icon">
                          <img src="images/icon_03.png" alt="up" />
                        </span>{" "}
                        Birds
                      </a>
                    </li>
                    <li>
                      <a href="#">
                        <span className="list_icon">
                          <img src="images/icon_04.png" alt="up" />
                        </span>{" "}
                        Rabbit
                      </a>
                    </li>
                    <li>
                      <a href="#">
                        <span className="list_icon">
                          <img src="images/icon_05.png" alt="up" />
                        </span>{" "}
                        Others
                      </a>
                    </li>
                  </ul>
                </div>
              </div>
              <div className="rght_cate">
                <div className="rght_cate_hd" id="opn_cat_bg">
                  Featured
                </div>
                <div className="sub_dwn">
                  <div className="feat_sec">
                    <div className="feat_sec_img">
                      <img src="images/feat_img1.png" alt="image" />
                    </div>
                    <div className="feat_txt">Lorem Ipusum Text</div>
                  </div>
                  <div className="feat_sec">
                    <div className="feat_sec_img">
                      <img src="images/feat_img2.png" alt="image" />
                    </div>
                    <div className="feat_txt">Lorem Ipusum Text</div>
                    <div className="btm_rgt">
                      <div className="btm_arc">Dogs</div>
                    </div>
                  </div>
                  <div className="feat_sec">
                    <div className="feat_sec_img">
                      <img src="images/feat_img3.png" alt="image" />
                    </div>
                    <div className="feat_txt">Lorem Ipusum Text</div>
                    <div className="btm_rgt">
                      <div className="btm_arc">Rabbits</div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <TimelineRoutes />
          </div>
        </div>
      </div>
    </div>
  );
}

const mapStatetoProps = (state) => {
  const { uploadImage } = state;
  return {
    uploadImage: uploadImage,
  };
};

export default Timeline;
