import React from 'react'
import axios from 'axios'

export default class UploadImage extends React.Component{

  constructor(props){
    super(props);
    this.state = {
      imageName:'',
      description:'',
      filename:'',
      date: new Date()
    }
  }

  handleSubmit = event =>{
    event.preventDefault();
    console.log(event.target[2].files[0].name);
    const user = {
      imageName:this.state.imageName,
      description:this.state.description,
      filename:event.target[2].files[0].name
    }
    axios
      .post("http://localhost:8887/home/uploadimage",user)
      .then(res=>{
        //console.log(res);
        // this.setState({loginError:res.data});
      })
  }

  handleChange = event => {
    this.setState({[event.target.name]:event.target.value});
  }

  render(){
    return(
      <div>
        <form onSubmit={this.handleSubmit} enctype="multipart/form-data">
          <label>Name:</label>
          <input type='text' name="imageName" onChange={this.handleChange} />
          <label>Description:</label>
          <textarea name="description" onChange={this.handleChange} /><br/>
          <input type='file' name='uploadedImage'/><br/>
          <input type='submit' value='submit'/>
        </form>
      </div>
    );
  }
}
