import React from 'react';
import axios from 'axios';
import history from '../../History/history'
import './register.css'
import {Link} from 'react-router-dom'
import LoginSignup from '../LoginSignup/loginsignup'

class Register extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      username:'',
      password:'',
      email:'',
      fname:'',
      lname:''
    }
    this.state={
      signupCheck: "",
      checkValue: false
    }
  }

  checkStatus = event => {
    this.setState({checkValue:event.target.checked})
  }

  handleSubmit = event =>{
    event.preventDefault();
    const user = {
      username:this.state.username,
      password:this.state.password,
      email:this.state.email,
      fname:this.state.fname,
      lname:this.state.lname
    }
    if(this.state.checkValue){
      axios
        .post("http://localhost:8887/home/receivedata",user)
        .then(res=>{
          this.setState({signupCheck:res.data});
          console.log(this.state.signupCheck)
      })
      .then(()=>{(this.state.signupCheck === "Details entered")?history.push('/profile'):history.push('/')});
    } else {
      this.setState({signupCheck:"Please accept the Terms and Conditions"});
    }
  }
  handleChange = event => {
    this.setState({[event.target.name]:event.target.value});
  }
  render(){
    return (
      <div class="main">
        <div className="container">
          <div className="content">

            <div className="content_lft">
              <h1>Welcome from PPL!</h1>
              <p className="discrptn">There are many variations of passages of Lorem Ipsum available, but the majority have suffered alteration in some form, by injected humour, or randomised words which don't look even slightly believable. If you are going to use a passage of Lorem Ipsum, you need to be sure there isn't anything embarrassing hidden in the middle of text. </p>
              <img src="./images/img_9.png" alt /> 
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Register;
