import React from 'react';
import axios from 'axios';
import history from '../../History/history'
import './login.css'
import {Link} from 'react-router-dom'

class Login extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      email:'',
      password:'' 
    }
    this.state={
      loginError: ""
    }
}
  handleSubmit = event =>{
    event.preventDefault();
    const user = {
      email:this.state.email,
      password:this.state.password
    }
    axios
      .post("http://localhost:8887/home/checkuser",user)
      .then(res=>{
        console.log(res.data);
        this.setState({loginError:res.data});
      })
      .then(()=>{(this.state.signupCheck === "Login Successful")?history.push('/profile'):history.push('/')});
  }

  handleChange = event => {
    this.setState({[event.target.name]:event.target.value});
  }
  render(){
    return(
      <div>
        <div className="container">
          <div className="content">
            <div className="content_rgt">
              <div className="login_sec">
                <h1>Log In</h1>
                <form onSubmit={this.handleSubmit}>
                  <li><span>Email-ID</span><input type="text" name="email" placeholder="Enter your email" onChange={this.handleChange}/></li>
                  <li><span>Password</span><input type="text" name="password" placeholder="Enter your password" onChange={this.handleChange}/></li>
                  <li><input type="checkbox" />Remember Me</li>
                  {this.state.loginError==="Login Successful"?(
                    <h4 className="green">{this.state.loginError}</h4>
                    ):(
                    <h4 className="red">{this.state.loginError}</h4>
                  )}
                  <li><input type="submit" defaultValue="Log In" /><a href>Forgot Password</a></li>
                </form>
                <div className="addtnal_acnt">I do not have any account yet.<Link to="/">Create My Account Now !</Link></div>
              </div>
            </div>
            <div className="content_lft">
              <h1>Welcome from PPL!</h1>
              <p className="discrptn">There are many variations of passages of Lorem Ipsum available, but the majority have suffered alteration in some form, by injected humour, or randomised words which don't look even slightly believable. If you are going to use a passage of Lorem Ipsum, you need to be sure there isn't anything embarrassing hidden in the middle of text. </p>
              <img src="images/img_9.png" alt /> 
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Login;
