import React, { useState } from "react";
import config from "../../Config/config";
import axios from "axios";
import Modal from "react-modal";

export default function ForgotPassword() {
  const [email, setEmail] = useState("");
  const [openModal, setOpenModal] = useState(false);

  function handleSubmit(event) {
    event.preventDefault();
    axios
      .post(`${config.backendUrl}forgot`, { email: email })
      .then((res) => {

      })
      .then(() => setOpenModal(true));
  }

  return (
    <div>
      <Modal isOpen={openModal} className="custom-modal-style">
        <div className="popup_sec" id="pop_forgt">
          <div className="clos_btn">
            <img
              src="images/clos.png"
              alt
              id="clos_pop"
              onClick={() => setOpenModal(false)}
            />
          </div>
          <div className="pop_hdr">
            A verififcation code has been sent to your e-mail Id for Reset Password Link
          </div>
          <div className="man_contnt">
            <span>Please Enter the Verification Code</span>
            <input type='text' /><br/>
            <input
              type="submit"
              defaultValue="Ok"
              placeholder="Verification Code"
              onClick={() => setOpenModal(false)}
            />
          </div>
        </div>
      </Modal>
      <div className="content_rgt">
        <div className="register_sec">
          <h1>Forgot Password</h1>
          <form onSubmit={handleSubmit}>
            <li>
              <span>Enter E-mail ID</span>
              <input
                type="email"
                placeholder="User@gmail.com"
                onChange={(event) => setEmail(event.target.value)}
              />
            </li>
            <li>
              <input type="submit" defaultValue="Submit" />
            </li>
          </form>
        </div>
      </div>
    </div>
  );
}
